    private void fcconActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_fcconActionPerformed
        //takes number put into farenheit text box and does math to convert to celsius
        String fahr = fhcon.getText();
        double fccon = Double.parseDouble(fahr);
        double fc = (fccon - 32) * 0.55;
//prints results
        clcon.setText("" + fc);
        fhcon.setText("");

    }//GEN-LAST:event_fcconActionPerformed

