    private void cfconActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cfconActionPerformed
        // TODO add your handling code here:
        //converts number put into celcius box and does math to convert it into farenheit
        String celc = clcon.getText();
        double cfcon = Double.parseDouble(celc);
        double cls = (cfcon * 1.8) + 32;
        //prints results
        fhcon.setText("" + cls);
        clcon.setText("");
    }//GEN-LAST:event_cfconActionPerformed

